parameters.jsCode:
// Input: {"user_query": "...", "user_id": "...", "region": "..."}
const query = $json["user_query"] || "";

// Extract genres
let genres = [];
if (/comedy/i.test(query)) genres.push("Comedy");
if (/drama/i.test(query)) genres.push("Drama");
if (/action/i.test(query)) genres.push("Action");
if (/romance/i.test(query)) genres.push("Romance");

// Extract languages
let languages = [];
if (/hindi/i.test(query)) languages.push("hi");
if (/english/i.test(query)) languages.push("en");

// Extract mood
let mood = [];
if (/feel[- ]?good/i.test(query)) mood.push("feel-good");
if (/dark/i.test(query)) mood.push("dark");

// Preference for TV vs Movie
let prefer_tv = /series|show|episodes?/i.test(query);

return [
  {
    json: {
      user_id: $json["user_id"],
      region: $json["region"],
      genres,
      languages,
      mood,
      prefer_tv,
      raw_query: query
    }
  }
];

// Safely handle missing upstream data
const movies = items[0]?.json?.results || [];
const shows = items[1]?.json?.results || [];

function normalize(item, type) {
  return {
    id: item.id,
    type,
    title: item.title || item.name,
    year: (item.release_date || item.first_air_date || "").split("-")[0],
    genres: item.genre_ids || [],
    rating: item.vote_average,
    overview: item.overview,
    poster: item.poster_path ? `https://image.tmdb.org/t/p/w500${item.poster_path}` : null,
    popularity: item.popularity
  };
}

return [
  {
    json: {
      merged: [
        ...movies.map(m => normalize(m, "movie")),
        ...shows.map(s => normalize(s, "tv"))
      ]
    }
  }
];

const prefs = $json || {};
const items = $json.merged || [];

function score(item, prefs) {
  let s = 0;
  if (prefs.genres?.some(g => item.genres?.includes(g))) s += 3;
  if (prefs.languages?.includes(item.original_language || "")) s += 2;
  if (prefs.mood?.length && item.overview?.toLowerCase().includes("light")) s += 1;
  s += (item.rating || 0) * 0.5;
  s += (item.popularity || 0) * 0.1;
  return s;
}

// Rank and take top N (e.g., 2 to reduce tokens)
const ranked = items
  .map(i => ({ ...i, _score: score(i, prefs) }))
  .sort((a, b) => b._score - a._score)
  .slice(0, 2); // limit items for LLM

// Return as **single item** for LLM node
return [
  {
    json: {
      raw_query: $json.raw_query,
      merged: ranked, // array of top items
    },
  },
];

parameters.text:
=Respond ONLY in valid JSON. Do not include extra text or numbering.

You are a movie recommender.
The user query was: {{$json.raw_query}}
Here is a list of top items:

{{$json.merged}}

Respond ONLY in JSON with the following structure:
{
  "results": [
    {
      "id": 1,
      "title": "Movie title",
      "year": 2025,
      "genres": ["Genre1", "Genre2"],
      "overview": "Brief description",
      "ratings": "Rating if available",
      "why": "One sentence why this matches the user query"
    }
  ]
}

- Use the data from the merged array.  
- Keep all movie data inside `results`.  
- Do NOT include any extra text outside the JSON.

