// If the item already has proper JSON, just return it
if ($json.results) {
  return [{ json: $json }];
}

// Otherwise, if the LLM output was wrapped in text (edge case)
let llmText = $json.text || "";

// Remove wrappers if any
llmText = llmText.replace(/```json\s*/g, "").replace(/```/g, "").trim();

let parsed;
try {
  parsed = JSON.parse(llmText);
} catch (e) {
  return [{ json: { error: "Invalid JSON from LLM", raw: llmText } }];
}

return [{
  json: {
    body: JSON.stringify($json)  // ensures valid JSON string
  }
}];
